package arrays;

public class SingleDimensionalArrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a[] = new int[5];
		a[0]=1;
		a[1]=2;
		a[2]=3;
		a[3]=4;
		a[4]=5;
		
		int a1[] = {1,2,3,5,5};
		
		for(int b:a1) {
			System.out.println(b);
		}

	}

}
