package typecasting;

public class TypeCastConncept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//upcasting smaller to larger
		int a = 10;
		long longVal=a;
		System.out.println(longVal);
		
		//downcasting>larger to smaller
		
		long l = 1234567;
		int i =(int) l;
		
		double dv =123.55;
		float ft =(float) dv;

	}

}
